import React from 'react';

export default class Counter extends React.Component {
    state = {
        counter: 0,
    };

    increment = () => {
        this.setState((prevState) => ({
            counter: prevState.counter + 1,
        }));
    };

    render() {
        const { render } = this.props;
        return render();
    }
}
